const entrada = $input.first().json;

// Esperado do Or√°culo:
// {
//   telefone_cliente,
//   nome_cliente,
//   mensagem_usuario,
//   historico_conversa (opcional),
//   contexto_extra (opcional)
// }

const telefone = entrada.telefone_cliente || entrada.telefone || "";
const nome = entrada.nome_cliente || "Cliente";
const mensagem = entrada.mensagem_usuario || entrada.mensagem || "";
const historico = entrada.historico_conversa || [];
const contextoExtra = entrada.contexto_extra || {};

// Monta um contexto condensado para o prompt
const historicoFormatado = historico
  .slice(-6)
  .map((msg) => `(${msg.origem || 'user'}) ${msg.texto}`)
  .join("\n");

const promptSistema = `Voc√™ √© um assistente de suporte t√©cnico do ecossistema GestorConecta.
Seu objetivo √© ajudar o usu√°rio de maneira objetiva, educada e clara.

Regras importantes:
- Responda SEMPRE em portugu√™s do Brasil.
- Seja direto, mas cordial.
- Se precisar pedir mais informa√ß√µes, pergunte de forma clara e em poucas linhas.
- Se o problema for t√©cnico demais, explique em linguagem simples.
- Evite respostas muito longas. Priorize clareza e praticidade.
- Se for uma d√∫vida sobre GestorConecta, tente explicar o passo a passo.
`;

let promptUsuario = `Nome do cliente: ${nome}
Telefone: ${telefone}
Mensagem atual: ${mensagem}`;

if (historicoFormatado) {
  promptUsuario += `\n\nHist√≥rico recente de conversa:\n${historicoFormatado}`;
}

if (Object.keys(contextoExtra).length > 0) {
  promptUsuario += `\n\nContexto extra fornecido pelo sistema:\n${JSON.stringify(contextoExtra, null, 2)}`;
}

return {
  json: {
    telefone_cliente: telefone,
    nome_cliente: nome,
    mensagem_usuario: mensagem,
    prompt_sistema: promptSistema,
    prompt_usuario: promptUsuario
  }
};

const respostaOpenAI = $input.first().json;
const contexto = $('Preparar Contexto + Prompt').first().json;

// OpenAI retorna algo como:
// { choices: [ { message: { content: "..." } } ], ... }

let mensagemIA = 'Ops, n√£o consegui gerar uma resposta agora. Tente novamente em alguns instantes.';

try {
  const choice = respostaOpenAI.choices?.[0];
  const content = choice?.message?.content;
  if (content) {
    mensagemIA = content.trim();
  }
} catch (error) {
  console.log('Erro ao ler resposta da IA:', error.message);
}

return {
  json: {
    telefone_destino: contexto.telefone_cliente,
    mensagem_resposta: mensagemIA,
    timestamp: new Date().toISOString()
  }
};
